import React, {useState, useEffect} from "react";
import axios from "axios";
import { Link } from "react-router-dom";

interface IPosts{
    id:number,
    title:string,
    content:string,
    image:string
}

const ListPost=()=>{

    const [loading, setLoading]=useState(true);
    const [posts, setPosts]=useState<IPosts[] | any>();

    useEffect(()=>{
        getAllPosts();
    }, [])

    const getAllPosts = async()=>{
        await axios.get('http://127.0.0.1:8000/api/posts').then(
            (res)=>{
                setPosts(res.data.posts);
                setLoading(false);
                console.log(res.data.posts);
            }
        )

    }

    if(loading)
    {
        return <h4>Chargement des donn√©es...</h4>
    }
    else
    {
        var posts_HTMLTABLE = any;
       
        posts_HTMLTABLE = posts.map( (post, index) => {
            return (
                <tr key={index}>
                    <td>{post.id}</td>
                    <td>{post.title}</td>
                    <td>{post.content}</td>
                    <td>{post.image}</td>
                    <td>
                        <Link to="" className="btn btn-success btn-sm">Edit</Link>
                    </td>
                    <td>
                        <button type="button" className="btn btn-danger btn-sm">Delete</button>
                    </td>
                </tr>
            );
        });
    }

    return (<></>)
}
export default ListPost;
